package sushmi;

public class Switch1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		switch (a) {
		case 1 :
			System.out.println("black");
			break;
		case 2:
			System.out.println("white");
			break;
		case 3:
			System.out.println("pink");
			break;
			
			
		}

	}

}
