package sushmitha;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int time = 3;
		switch(time) {
		case 1:
			System.out.println("gud mrg ");
			break;
		case 2:
			System.out.println("gud noon ");
			break;
		default:
			System.out.println("gud night");
			
			
		}

	}

}
